mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY notice_seq) AS rowNum, notice_seq AS noticeSeq, member_id AS memberId, title, content, writeTime, views FROM notice
isDeleted = 0
AND writeTime BETWEEN #{startDate} AND #{endDate}
AND title LIKE CONCAT('%', #{searchKeyword}, '%')
AND title LIKE CONCAT('%', #{searchKeyword}, '%') OR content LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY notice_seq DESC LIMIT #{start}, #{limit}

mapper.update:
UPDATE notice 
SET title=#{title}, content=#{content}
WHERE notice_seq=#{noticeSeq}
AND isDeleted = 0

UPDATE notice 
SET isDeleted = 1
WHERE notice_seq = #{loginId}

 UPDATE notice 
 SET isDeleted = 1
	 WHERE writeTime <= #{deleteDay}
