mapper.select:
    	SELECT COUNT(notice_seq)
    	FROM notice

			isDeleted = 0


	                AND writeTime BETWEEN #{startDate} AND #{endDate}


	                AND writeTime >= #{startDate}




	                AND writeTime <= #{endDate}





	                   AND title LIKE CONCAT('%', #{searchKeyword}, '%')


	                    AND title LIKE CONCAT('%', #{searchKeyword}, '%')
	                    OR content LIKE CONCAT('%', #{searchKeyword}, '%')

    	SELECT ROW_NUMBER() OVER (ORDER BY notice_seq) AS rowNum,
    				notice_seq AS noticeSeq, member_id AS memberId, title, content, writeTime, views
    	FROM notice 

    		isDeleted = 0


	                AND writeTime BETWEEN #{startDate} AND #{endDate}


	                AND writeTime >= #{startDate}




	                AND writeTime <= #{endDate}





	                   AND title LIKE CONCAT('%', #{searchKeyword}, '%')


	                    AND title LIKE CONCAT('%', #{searchKeyword}, '%')
	                    OR content LIKE CONCAT('%', #{searchKeyword}, '%')




    	ORDER BY notice_seq DESC 
    	LIMIT #{start}, #{limit}

SELECT originFileName, storedFileName 
FROM file
WHERE notice_seq = #{noticeSeq}

SELECT originFileName 
FROM file 
WHERE storedFileName = #{storedFileName}

mapper.update:
UPDATE notice 
SET title=#{title}, content=#{content}
WHERE notice_seq=#{noticeSeq}
AND isDeleted = 0

UPDATE notice 
SET isDeleted = 1
WHERE notice_seq = #{noticeSeq}

 UPDATE notice 
 SET isDeleted = 1
	 WHERE writeTime <= #{deleteDay}

	UPDATE notice 
    SET isDeleted = 1
	WHERE member_id = #{memberId}
