mapper.select:
SELECT id, writer, password, title, content, writeTime, views
FROM board 
WHERE id=#{id}

mapper.update:
UPDATE board 
SET views = views + 1 
WHERE id=#{id}

UPDATE board 
SET title=#{title}, content=#{content}
WHERE id=#{id}

mapper.delete:
DELETE FROM board 
WHERE id=#{id}
AND id = #{loginId}
