mapper.select:
SELECT notice_seq AS noticeSeq, member_id AS memberId, password, title, content, writeTime, views
FROM notice 
WHERE notice_seq=#{noticeSeq}

mapper.update:
UPDATE notice 
SET views = views + 1 
WHERE notice_seq=#{noticeSeq}

UPDATE notice 
SET title=#{title}, content=#{content}
WHERE notice_seq=#{noticeSeq}
